package MitaliPrograms;

//Write Java program to display numbers from 1 to 100

public class Number1To100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		System.out.println("The numbers are given below:");

		for (i = 1; i <= 100; i++) {
			System.out.println(i);
		}

	}

}